#include <svdpi.h>
void logic_op_dpi(int data1, int data2, int sel, int *data) {

  if(sel==0)
    *data = data1 & data2;
  else if(sel==1)
    *data = data1 | data2;
  else if(sel==2)
    *data = data1 ^~ data2;
  else if(sel==3)
    *data = data1 ^ data2;
}
